import {React, useState} from "react";
import {Pixel, WHITE_VALUE, BLACK_VALUE}  from "./Pixel";
import "./styles/Drawer.css"

function Drawer() {
  const rows = 28;
  const columns = 28;

  let firstPixelsValues = [];
  for (let i = 0; i < rows * columns; i++){
    firstPixelsValues.push({value: WHITE_VALUE});
  }

  const [pixels, setPixels] = useState(firstPixelsValues)

  function updatePixels(index, value){
    const updatedPixels = [...pixels];
    updatedPixels[index] = value;
    setPixels(updatedPixels);
    //console.log(updatedPixels)
  }

  // Используйте useEffect для отслеживания изменений pixels
  useEffect(() => {
    console.log(pixels);
  }, [pixels]); // Эффект сработает каждый раз при изменении pixels

  return (
    <div className="Drawer">
      {pixels.map((el, i) => (
        <Pixel key={i} ind={i} value={el.value} onUpdate={updatePixels}/>
      ))}
    </div>
  );
}

export default Drawer;
